mapper.update:
UPDATE advise
SET title = #{title}, content = #{content}
WHERE advno = #{advno}

UPDATE advise
SET viewcnt = viewcnt +1
WHERE advno = #{advno}

UPDATE advise
SET ansnum = ansnum + 1
WHERE groupno = #{groupno} AND ansnum > #{ansnum}

mapper.select:
	SELECT COUNT(advno) as cnt
    FROM advise
    WHERE advno=#{advno} AND id=#{id}
