mapper.select:
select carno, place, brand, placevalue, state
from car
where carno = #{carno}

select carno, place, brand, placevalue, state, r
from(
	select carno, place, brand, placevalue, state, rownum r
	from(
		select carno, place, brand, placevalue, state
		from car
		order by carno desc
	)
)

where r>=#{sno} and r<=#{eno}
