mapper.update:
update car_booked
set ID=#{ID}
where carbook_no = #{carbook_no}

mapper.select:
select carbook_no, carno, ID, carbookdate
from car_booked
where carbook_no = #{carbook_no}

select carbook_no, carno, ID, carbookdate, r
	from(
	select carbook_no, carno, ID, carbookdate, rownum r
	from(
		select carbook_no, carno, ID, carbookdate
		from car_booked
		order by carbook_no desc
	)
)

where r>=#{sno} and r<=#{eno}
