mapper.update:
UPDATE qquestion
SET viewcnt = viewcnt +1
WHERE qqno = #{qqno}

UPDATE qquestion
SET title = #{title}, content = #{content}
WHERE qqno = #{qqno}

mapper.select:
		SELECT qqno, title, viewcnt, qqdate, r
		from(
			SELECT qqno, title, viewcnt, qqdate, rownum r 
			from(
				SELECT qqno, title, viewcnt, qqdate
				FROM qquestion
				
					
						
							title like '%' || #{word} || '%'
						
						
							content like '%' || #{word} || '%'
						
					
				
				ORDER BY qqno DESC

				)
			)                                                                            
   		                                                                        
  		 where r>=#{sno} and r<=#{eno}
