mapper.update:
	update reply
	set content = #{content}
	where rnum = #{rnum}

mapper.select:
select rnum, content, to_char(regdate,'yyyy-mm-dd') regdate, id, bbsno, r
FROM(
	select rnum, content, regdate, id, bbsno, rownum r
	FROM(
		select rnum, content, regdate, id, bbsno
		from REPLY
		where bbsno = #{bbsno}
		order by rnum DESC
	)

)WHERE r >= #{sno} and r <= #{eno}
