mapper.update:

update sulmunr
set chocontent = #{chocontent}
where sulmunrno = #{sulmunrno}

mapper.select:
select sulmunrno , chono , chocontent , sulno,r FROM( select sulmunrno , chono , chocontent , sulno, rownum r FROM( select sulmunrno , chono , chocontent , sulno from sulmunr
chocontent LIKE '%' || #{word} || '%'
ORDER BY sulmunrno asc
) )
where r>=#{sno} and r<=#{eno}
